	@SuppressWarnings({ "unchecked", "unused" })
	public static List<Map<String, Double>> getSleepList() throws Exception {
		
		Map<String, Object> map = (Map<String, Object>) JsonUtil.asMap(json);
		Map<String, Object> dataMap = (Map<String, Object>)map.get("data");
		Map<String, Object> summaryMap = (Map<String, Object>) dataMap.get("summary");
		Map<String, Object> detailMap = (Map<String, Object>) dataMap.get("detail");
		Map<String, Object> analysisMap = (Map<String, Object>) dataMap.get("analysis");
		
		System.out.println("recordCount:" + summaryMap.get("recordCount"));
		
		Integer startTime = (Integer) summaryMap.get("startTime");
		System.out.println("startTime:" + timeToDate(startTime));
		
		System.out.println("duration:" + analysisMap.get("duration"));
		
		Integer wakeupTimeStamp = (Integer) analysisMap.get("wakeupTimeStamp");
		System.out.println("wakeupTimeStamp:" + timeToDate(wakeupTimeStamp));
		
		List<Double> sleepStatus = (List<Double>) analysisMap.get("sleepCurveArray");
		System.out.println("sleepCurveArraySize: " + sleepStatus.size());
		
		List<Map<String, Double>> sleepList = new ArrayList<Map<String, Double>>();
		
		Calendar cal = Calendar.getInstance();
		Long millisSecond = Long.valueOf(startTime+"")*1000;
		cal.setTimeInMillis(millisSecond);
		
		for(Double oneStatu : sleepStatus) {
			Map<String, Double> oneSleep = new HashMap<String, Double>();
			oneSleep.put(dateToStr(cal.getTime()), oneStatu);
			sleepList.add(oneSleep);
			cal.add(Calendar.MINUTE, 1);
		}
		return sleepList;
	}
	
	private static String timeToDate(Integer second) {
		Long millisSecond = Long.valueOf(second+"")*1000;
		return timeToDate(millisSecond);
	}
	
	private static String timeToDate(Long millisecond) {
		Calendar cal = Calendar.getInstance();
		cal.setTimeInMillis(millisecond);
		SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss"); 
		
		return sdf.format(cal.getTime());
	}
	
	private static String dateToStr(Date date) {
		Calendar cal = Calendar.getInstance();
		SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss"); 
		return sdf.format(date);
	}
